mapper.insert:
insert into ate 
set dnum=#{dnum}, mnum=#{mnum}, ate_date=now(),
ate_picture=#{ate_picture}, ate_content=#{ate_content}

mapper.update:
UPDATE ate
set ate_editdate=now(),
ate_picture=#{ate_picture}, ate_content=#{ate_content}
WHERE ate_num = #{ate_num}

UPDATE ate
SET ate_hit = ate_hit + 1
WHERE ate_num = #{ate_num}
