mapper.update:
UPDATE dish
SET title=#{title}, ingredient=#{ingredient}, recipe=#{recipe},url=#{url},picture=#{picture},
editdate=NOW(),t_time=#{t_time}, m_ingredient=#{m_ingredient}, cookery=#{cookery}, t_weather=#{t_weather}
WHERE dnum = #{dnum}

UPDATE dish
SET hit = hit + 1
WHERE dnum = #{dnum}
