mapper.select:
SELECT A.ANUM, A.ADMINID, A.ADMINPW, A.NICKNAME, A.ROLE, A.DATE, A.MODDATE, A.LASTACCESSDATE, COUNT(D.DISH) 
FROM ADMIN A LEFT OUTER JOIN DISH D
WHERE A.ANUM = #{anum}

SELECT A.ANUM, A.ADMINID, A.ADMINPW, A.NICKNAME, A.ROLE, A.DATE, A.MODDATE, A.LASTACCESSDATE, COUNT(D.DISH) 
FROM ADMIN A LEFT OUTER JOIN DISH D
WHERE A.ADMINID = #{adminID}
